import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Switch, Route, Redirect } from "react-router-dom";
import { Register } from "./components/Register";
import { Dashboard } from "./components/Dashboard";
import { NavbarRC } from "./components/Navbar";
import { Auth } from "./components/Auth";
import { Right } from "./components/Right";
import { KubeFlow } from "./components/KubeFlow";

function App() {
  const [authenticated, setAuthenticated] = useState(false);

  useEffect(() => {
    const checkAuthentication = () => {
      const storedToken = localStorage.getItem("token");
      setAuthenticated(!!storedToken);
    };

    checkAuthentication();

    // Escucha los cambios en el almacenamiento local
    window.addEventListener('storage', checkAuthentication);

    // Limpia el evento al desmontar
    return () => {
      window.removeEventListener('storage', checkAuthentication);
    };
  }, []);

  return (
    <Router style={{ backgroundColor: 'black', height: '100vh', width: '100vw' }}>
      {authenticated ? (
        <>
          <NavbarRC />
          <div className="flex">
            <div className="w-4/5">
              <Switch>
                <Route exact path="/" component={Dashboard} />
                <Route path="/dashboard" component={Dashboard} />
                <Route path="/kubeflow" component={KubeFlow} />
              </Switch>
            </div>
            <div className="w-1/5">
              <Right />
            </div>
          </div>
        </>
      ) : (
        <div className="flex justify-center items-center h-screen">
          <Switch>
            <Route exact path="/" component={Auth} />
            <Route path="/auth" component={Auth} />
            <Route path="/register" component={Register} />
          </Switch>
        </div>
      )}
    </Router>
  );
}

export default App;
