import React, { useState, useEffect } from "react";
import axios from "axios";
import { Table, Button, Card } from "flowbite-react";

export const KubeFlow = () => {
  const [containers, setContainers] = useState([]);

  const scanContainers = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:5001/containers");
      return response.data;
    } catch (error) {
      console.error(error);
      return [];
    }
  };

  const handleScan = async () => {
    const containers = await scanContainers();
    setContainers(containers);
  };

  return (
    <div className="flex justify-center items-center h-screen">
      <Card className="w-full max-w-md">
        <Card.Header>
          <h2 className="text-xl font-bold">Contenedores</h2>
        </Card.Header>
        <Card.Body>
          <Button onClick={handleScan} color="primary" className="mb-4">
            Scan
          </Button>
          <Table hoverable>
            <Table.Head>
              <Table.HeadCell>Nombre del contenedor</Table.HeadCell>
            </Table.Head>
            <Table.Body className="divide-y">
              {containers.map((container, index) => (
                <Table.Row key={index}>
                  <Table.Cell>{container.Names}</Table.Cell>
                </Table.Row>
              ))}
            </Table.Body>
          </Table>
        </Card.Body>
      </Card>
    </div>
  );
};
