import React, { useState } from "react";
import { Avatar, Navbar, Dropdown } from 'flowbite-react';

export const NavbarRC = () => {
  const [leftSidebarOpen, setLeftSidebarOpen] = useState(false);
  const [rightSidebarOpen, setRightSidebarOpen] = useState(false);

  const toggleLeftSidebar = () => {
    setLeftSidebarOpen(!leftSidebarOpen);
  };

  const toggleRightSidebar = () => {
    setRightSidebarOpen(!rightSidebarOpen);
  };

  return (
    <>
      <Navbar fluid rounded>
        <Navbar.Brand href="#">
          <button onClick={toggleLeftSidebar}>☰</button>
          <span className="self-center whitespace-nowrap text-xl font-semibold dark:text-white">KubeHub</span>
        </Navbar.Brand>
        <div className="flex md:order-2">
          <Dropdown
            arrowIcon={false}
            inline
            label={
              <button onClick={toggleRightSidebar}>
                <Avatar alt="User settings" img="https://flowbite.com/docs/images/people/profile-picture-5.jpg" rounded />
              </button>
            }
          >
            {/* Aquí van las opciones del menú desplegable */}
          </Dropdown>
        </div>
      </Navbar>
      {leftSidebarOpen && (
        <div className="sidebar-left">
          {/* Aquí va el contenido de la barra lateral izquierda */}
        </div>
      )}
      {rightSidebarOpen && (
        <div className="sidebar-right">
          {/* Aquí va el contenido de la barra lateral derecha */}
        </div>
      )}
    </>
  );
};
