import React, { useState } from "react";
import { Card, Button, TextInput } from "flowbite-react";

export const Kubeia = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  const handleSend = () => {
    if (input) {
      setMessages([...messages, { text: input, sender: "user" }]);
      setInput("");
    }
  };

  return (
    <div className="flex flex-col p-8">
      <Card className="w-full space-y-4 rounded-xl shadow-md dark:bg-gray-800">
        <div className="d-flex flex-column h-100">
          <div className="overflow-auto mb-3">
            {messages.map((message, index) => (
              <div key={index} className={`text-${message.sender === "user" ? "end" : "start"}`}>
                <Card className={`mb-2 ${message.sender === "user" ? "bg-primary text-white" : ""}`}>
                  {message.text}
                </Card>
              </div>
            ))}
          </div>
          <div className="mt-auto">
            <TextInput 
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Type a message"
              onKeyPress={(event) => event.key === 'Enter' ? handleSend() : null}
            />
            <Button onClick={handleSend} className="mt-2">Send</Button>
          </div>
        </div>
      </Card>
    </div>
  );
};
