import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Switch, Route, Redirect } from "react-router-dom";

import { Register } from "./components/Register";
import { Dashboard } from "./components/Dashboard";
import { NavbarWithDropdown } from "./components/Navbar";
import { FQA } from "./components/fqa";
import { Submissions } from "./components/Submission";
import { Rankings } from "./components/Rankings";
import { Problems } from "./components/Problems";
import { Account } from "./components/Account";
import { Problem } from "./components/Problem";
import { Auth } from "./components/Auth";
import { Right } from "./components/Right";

function App() {
  const [authenticated, setAuthenticated] = useState(false);

  useEffect(() => {
    const storedToken = localStorage.getItem("token");
    if (storedToken) {
      setAuthenticated(true);
    }
  }, []);

  return (
    <Router>
        {authenticated && <NavbarWithDropdown />}
        <div className="flex">
          <div className="w-4/5">
          <Switch>
            <Route exact path="/" component={authenticated ? Dashboard : Auth} />
            <Route path="/dashboard" component={Dashboard} />
            <Route path="/register" component={Register} />
            <Route path="/submissions" component={Submissions} />
            <Route path="/fqa" component={FQA} />
            <Route path="/rankings" component={Rankings} />
            <Route path="/problems" component={Problems} />
            <Route path="/account" component={Account} />
            <Route path="/problem/:id" component={Problem} />
            <Redirect to="/" />
          </Switch>
          </div>
          <div className="w-1/5">
            <Right />
          </div>
        </div>
    </Router>
  );
}

export default App;
