import React, { useState } from "react";
import { Table, Button, Spinner, Card, Badge } from "flowbite-react";

export const KubeSh = () => {
  const [input, setInput] = useState("");
  const [output, setOutput] = useState("");

  const handleInputChange = (event) => {
    setInput(event.target.value);
  };

  const handleCommandSubmit = (event) => {
    event.preventDefault();
    // Aquí puedes procesar el comando ingresado en el input
    // Por ahora, simplemente agregamos el comando al output
    setOutput((prevOutput) => prevOutput + "\n" + input);
    setInput("");
  };

  return (
    <div className="flex flex-col p-8">
      <Card className="w-full space-y-4 rounded-xl shadow-md dark:bg-gray-800">
        <textarea
          readOnly
          value={output}
          className="mb-4 w-full h-64 bg-black text-white"
        />
        <form onSubmit={handleCommandSubmit}>
          <input
            type="text"
            value={input}
            onChange={handleInputChange}
            className="w-full bg-black text-white"
          />
        </form>
      </Card>
    </div>
  );
};
