import React, { useEffect, useState } from "react";
import { Card } from "flowbite-react";
import { useParams } from "react-router-dom";
import axios from "axios";
import {
  Chart,
  PieController,
  ArcElement,
  LinearScale,
  Title,
  CategoryScale,
} from "chart.js";

const useMetrics = (id) => {
  const [metrics, setMetrics] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMetrics = async () => {
      try {
        const response = await axios.get(
            `http://localhost:5001/container/${id}/metrics`
        );
        console.log(response.data);
        setMetrics(response.data);
      } catch (error) {
        console.error(error);
        setError("Error fetching metrics. Please try again.");
      }
    };

    const intervalId = setInterval(fetchMetrics, 1000);

    return () => clearInterval(intervalId);
  }, [id]);

  return { metrics, error };
};

export const ContainerDetails = () => {
  const { id } = useParams();
  const { metrics, error } = useMetrics(id);
  const [cpuChart, setCpuChart] = useState(null);
  const [memChart, setMemChart] = useState(null);

  useEffect(() => {
    // Register the controllers
    Chart.register(PieController, ArcElement, LinearScale, Title, CategoryScale);
  }, []);

  useEffect(() => {
    if (metrics) {
      // If the charts have not been created yet, create them
      if (!cpuChart) {
        const cpuCtx = document.getElementById("cpuChart").getContext("2d");
        setCpuChart(new Chart(cpuCtx));
      } else {
        // If the charts have already been created, update their data
        cpuChart.data = {
          labels: ["CPUPerc"],
          datasets: [
            {
              label: "CPU Usage",
              data: [parseFloat(metrics.CPUPerc)],
              backgroundColor: ["rgb(255, 99, 132)"],
            },
          ],
        };
        cpuChart.update();
      }
  
      if (!memChart) {
        const memCtx = document.getElementById("memChart").getContext("2d");
        setMemChart(new Chart(memCtx));
      } else {
        memChart.data = {
          labels: ["MemPerc"],
          datasets: [
            {
              label: "Memory Usage",
              data: [parseFloat(metrics.MemPerc)],
              backgroundColor: ["rgb(54, 162, 235)"],
            },
          ],
        };
        memChart.update();
      }
    }
  }, [metrics]);

  return (
    <div className="flex flex-col p-8">
      <div className="flex space-x-4">
        <Card className="w-full max-w-sm">
          <h2 className="text-2xl font-bold tracking-tight text-gray-900 dark:text-white">
            {id} - CPU Usage
          </h2>
          <canvas id="cpuChart"></canvas>
        </Card>
        <Card className="w-full max-w-sm">
          <h2 className="text-2xl font-bold tracking-tight text-gray-900 dark:text-white">
            {id} - Memory Usage
          </h2>
          <canvas id="memChart"></canvas>
        </Card>
      </div>
      {error && <p>{error}</p>}
    </div>
  );
};
