import React, { useEffect, useState } from 'react';
import { Card } from 'flowbite-react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Chart, PieController, ArcElement, LinearScale, Title, CategoryScale } from 'chart.js';

// Register the controllers
Chart.register(PieController, ArcElement, LinearScale, Title, CategoryScale);

export const ContainerDetails = () => {
  const { id } = useParams();
  const [metrics, setMetrics] = useState(null);
  const [chart, setChart] = useState(null);

  useEffect(() => {
    const fetchMetrics = async () => {
      try {
        const response = await axios.get(`http://localhost:5001/container/${id}/metrics`);
        console.log(response.data);
        setMetrics(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    // Fetch metrics every second
    const intervalId = setInterval(fetchMetrics, 1000);

    // Clear interval on unmount
    return () => clearInterval(intervalId);
  }, [id]);

  useEffect(() => {
    if (metrics) {
      const ctx = document.getElementById('myChart').getContext('2d');

      // If the chart has not been created yet, create it
      if (!chart) {
        const newChart = new Chart(ctx, {
          type: 'pie',
          data: {
            labels: ['CPUPerc', 'MemPerc'],
            datasets: [{
              label: 'Container Metrics',
              data: [parseFloat(metrics.CPUPerc), parseFloat(metrics.MemPerc)],
              backgroundColor: ['rgb(255, 99, 132)', 'rgb(54, 162, 235)'],
            }]
          }
        });
        setChart(newChart);
      } else {
        // If the chart has already been created, update its data
        chart.data.datasets[0].data = [parseFloat(metrics.CPUPerc), parseFloat(metrics.MemPerc)];
        chart.update();
      }
    }
  }, [metrics]);

  return (
    <div className="flex flex-col p-8">
      <Card className="w-full space-y-4 rounded-xl shadow-md dark:bg-gray-800">
        <h2 className="text-2xl font-bold tracking-tight text-gray-900 dark:text-white">
          {id}
        </h2>
        <canvas id="myChart"></canvas>
      </Card>
    </div>
  );
};
