import React, { useEffect, useState } from 'react';
import { Card } from 'flowbite-react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Chart, LineController, LineElement, PointElement, LinearScale, Title } from 'chart.js';

// Register the controllers
Chart.register(LineController, LineElement, PointElement, LinearScale, Title);

export const ContainerDetails = () => {
  const { id } = useParams();
  const [metrics, setMetrics] = useState(null);

  useEffect(() => {
    const fetchMetrics = async () => {
      try {
        const response = await axios.get(`http://localhost:5001/container/${id}/metrics`);
        setMetrics(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchMetrics();
  }, [id]);

  useEffect(() => {
    if (metrics) {
      const ctx = document.getElementById('myChart').getContext('2d');
      new Chart(ctx, {
        type: 'line',
        data: {
          labels: Object.keys(metrics),
          datasets: [{
            label: 'Container Metrics',
            data: Object.values(metrics),
            fill: false,
            borderColor: 'rgb(75, 192, 192)',
            tension: 0.1
          }]
        }
      });
    }
  }, [metrics]);

  return (
    <div className="flex flex-col p-8">
      <Card className="w-full space-y-4 rounded-xl shadow-md dark:bg-gray-800">
        <h2 className="text-2xl font-bold tracking-tight text-gray-900 dark:text-white">
          {id}
        </h2>
        <p className="text-gray-500 dark:text-gray-400">
          Click the button below to fetch the local containers.
        </p>
        <canvas id="myChart"></canvas>
      </Card>
    </div>
  );
};
