import React, { useState, useEffect } from "react";
import { Table, Button } from "flowbite-react";
import axios from "axios";

export const KubeFlow = () => {
  const [containers, setContainers] = useState([]);

  const scanContainers = async () => {
    try {
      const response = await axios.get("http://localhost:5001/containers");
      return response.data;
    } catch (error) {
      console.error(error);
      return [];
    }
  };

  const handleScan = async () => {
    const containers = await scanContainers();
    setContainers(containers);
  };

  return (
    <div className="rounded-lg p-8">
      <div className="relative overflow-x-auto">
        <Button onClick={handleScan} color="primary" className="mb-4">
            Scan
        </Button>
        <Button onClick={handleScan} color="light">Light</Button>

        <Table hoverable>
          <Table.Head>
            <Table.HeadCell>
              <div className="flex items-center space-x-2">
                <svg
                  className="w-6 h-6 text-gray-800 dark:text-white"
                  aria-hidden="true"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 18 16"
                >
                  <path
                    stroke="currentColor"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M1 1v14h16m0-9-3-2-3 5-3-2-3 4"
                  />
                </svg>
                <span>Containers</span>
              </div>
            </Table.HeadCell>
          </Table.Head>
          <Table.Body className="divide-y">
          {containers.map((container, index) => (
                <Table.Row key={index}>
                  <Table.Cell>{container.Names}</Table.Cell>
                </Table.Row>
              ))}
          </Table.Body>
        </Table>
      </div>
    </div>
  );
};
