import React, { useState } from "react";
import { Table, Button, Spinner, Card } from "flowbite-react";
import axios from "axios";

export const KubeFlow = () => {
  const [containers, setContainers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showTable, setShowTable] = useState(false);

  const scanContainers = async () => {
    setLoading(true);
    try {
      const response = await axios.get("http://localhost:5001/containers");
      return response.data;
    } catch (error) {
      console.error(error);
      return [];
    } finally {
      setLoading(false);
    }
  };

  const handleScan = async () => {
    const containers = await scanContainers();
    setContainers(containers);
    setShowTable(true);
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-100">
      <Card className="w-full max-w-md p-8 space-y-4 bg-white rounded-xl shadow-md dark:bg-gray-800">
        <h2 className="text-2xl font-bold tracking-tight text-gray-900 dark:text-white">
          Local Containers
        </h2>
        <p className="text-gray-500 dark:text-gray-400">
          Click the button below to fetch the local containers.
        </p>
        <Button onClick={handleScan} color="light" className="flex items-center space-x-2">
          {loading ? <Spinner /> : "Get containers"}
          <svg
            class="w-6 h-6 text-gray-800 dark:text-white"
            aria-hidden="true"
            xmlns="http://www.w3.org/2000/svg"
            fill="currentColor"
            viewBox="0 0 18 18"
          >
            <path d="M6.143 0H1.857A1.857 1.857 0 0 0 0 1.857v4.286C0 ..."/>
          </svg>
        </Button>

        {showTable && (
          <Table hoverable className="mt-4">
            <Table.Head>
              <Table.HeadCell>
                <div className="flex items-center space-x-2">
                  <svg
                    className="w-6 h-6 text-gray-800 dark:text-white"
                    aria-hidden="true"
                    xmlns="http://www.w3.org/2000/svg"
                    fill="none"
                    viewBox="0 ..."/>
                  <span>Containers</span>
                </div>
              </Table.HeadCell>
            </Table.Head>
            <Table.Body className="divide-y">
              {containers.map((container, index) => (
                <Table.Row key={index}>
                  <Table.Cell>{container.Names}</Table.Cell>
                </Table.Row>
              ))}
            </Table.Body>
          </Table>
        )}
      </Card>
    </div>
)};
