import React, { useState } from "react";
import { Card, Button, TextInput } from "flowbite-react";

export const Kubeia = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [suggestions, setSuggestions] = useState([
    "¿Cómo instalo Docker?",
    "¿Cómo creo un pod en Kubernetes?",
    "¿Cómo escalo una aplicación en Kubernetes?",
    "¿Cómo comparto datos entre contenedores en Docker?"
  ]);

  const handleSend = () => {
    if (input) {
      setMessages([...messages, { text: input, sender: "user" }]);
      setInput("");
    }
  };

  return (
    <div className="vh-100 d-flex flex-column align-items-center justify-content-center">
      <Card className="p-5 shadow-lg" style={{ maxWidth: '800px', width: '100%' }}>
        <div className="d-flex flex-column h-100">
          <div className="overflow-auto mb-3">
            {messages.map((message, index) => (
              <div key={index} className={`text-${message.sender === "user" ? "end" : "start"}`}>
                <Card className={`mb-2 ${message.sender === "user" ? "bg-primary text-white" : ""}`}>
                  {message.text}
                </Card>
              </div>
            ))}
          </div>
          <div className="d-flex flex-wrap mb-3">
            {suggestions.map((suggestion, index) => (
              <Card key={index} className="m-1 p-2 bg-light">
                {suggestion}
              </Card>
            ))}
          </div>
          <div className="mt-auto d-flex align-items-center">
            <TextInput 
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Type a message"
              onKeyPress={(event) => event.key === 'Enter' ? handleSend() : null}
              className="me-2 flex-grow-1"
            />
            <Button onClick={handleSend}>Send</Button>
          </div>
        </div>
      </Card>
    </div>
  );
};
