import React, { useEffect, useState } from 'react';
import { Card } from 'flowbite-react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Chart, PieController, ArcElement, LinearScale, Title, CategoryScale } from 'chart.js';

// Register the controllers
Chart.register(PieController, ArcElement, LinearScale, Title, CategoryScale);

export const ContainerDetails = () => {
  const { id } = useParams();
  const [metrics, setMetrics] = useState(null);
  const [cpuChart, setCpuChart] = useState(null);
  const [memChart, setMemChart] = useState(null);

  useEffect(() => {
    const fetchMetrics = async () => {
      try {
        const response = await axios.get(`http://localhost:5001/container/${id}/metrics`);
        console.log(response.data);
        setMetrics(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    // Fetch metrics every second
    const intervalId = setInterval(fetchMetrics, 1000);

    // Clear interval on unmount
    return () => clearInterval(intervalId);
  }, [id]);

  useEffect(() => {
    if (metrics) {
      // If the charts have not been created yet, create them
      if (!cpuChart) {
        const cpuCtx = document.getElementById('cpuChart').getContext('2d');
        const newCpuChart = new Chart(cpuCtx, {
          type: 'pie',
          data: {
            labels: ['CPUPerc'],
            datasets: [{
              label: 'CPU Usage',
              data: [parseFloat(metrics.CPUPerc)],
              backgroundColor: ['rgb(255, 99, 132)'],
            }]
          }
        });
        setCpuChart(newCpuChart);
      } else {
        // If the charts have already been created, update their data
        cpuChart.data.datasets[0].data = [parseFloat(metrics.CPUPerc)];
        cpuChart.update();
      }

      if (!memChart) {
        const memCtx = document.getElementById('memChart').getContext('2d');
        const newMemChart = new Chart(memCtx, {
          type: 'pie',
          data: {
            labels: ['MemPerc'],
            datasets: [{
              label: 'Memory Usage',
              data: [parseFloat(metrics.MemPerc)],
              backgroundColor: ['rgb(54, 162, 235)'],
            }]
          }
        });
        setMemChart(newMemChart);
      } else {
        memChart.data.datasets[0].data = [parseFloat(metrics.MemPerc)];
        memChart.update();
      }
    }
  }, [metrics]);

  return (
    <div className="flex flex-col p-8">
      <Card className="max-w-sm">
        <h2 className="text-2xl font-bold tracking-tight text-gray-900 dark:text-white">
          {id} - CPU Usage
        </h2>
        <canvas id="cpuChart"></canvas>
      </Card>
      <Card className="max-w-sm">
        <h2 className="text-2xl font-bold tracking-tight text-gray-900 dark:text-white">
          {id} - Memory Usage
        </h2>
        <canvas id="memChart"></canvas>
      </Card>
    </div>
  );
};
