import React, { useState } from "react";
import { Card, Button } from "flowbite-react";

export const DockerRoomCard = ({ roomName, isPublic, onStateChange }) => {
  const [publicState, setPublicState] = useState(isPublic);

  const handleStateChange = () => {
    setPublicState(!publicState);
    onStateChange(roomName);
  };

  return (
    <Card className="w-full sm:w-1/2 md:w-1/3 lg:w-1/4 xl:w-1/4 p-4 m-2">
      <div className="flex flex-col h-full rounded shadow-md p-4">
        <h2 className="text-xl mb-2">{roomName}</h2>
        <p className="mb-2">{publicState ? "Public" : "Private"}</p>
        <Button className="mt-auto m-4">
          Join
        </Button>
        <Button onClick={handleStateChange} className="mt-auto m-4">
          Change to {publicState ? "Private" : "Public"}
        </Button>
      </div>
    </Card>
  );
};

export const KubeSh = () => {
  const rooms = [
    { name: "Sala 1", isPublic: true },
    { name: "Sala 2", isPublic: false },
    { name: "Sala 3", isPublic: true },
    { name: "Sala 4", isPublic: true },
    { name: "Sala 5", isPublic: true },
    { name: "Sala 6", isPublic: true },
  ];

  const handleRoomStateChange = (roomName) => {
    console.log(`Room state changed for ${roomName}`);
  };

  return (
    <div className="flex flex-wrap justify-center items-start p-8">
      {rooms.map((room) => (
        <DockerRoomCard
          key={room.name}
          roomName={room.name}
          isPublic={room.isPublic}
          onStateChange={handleRoomStateChange}
        />
      ))}
    </div>
  );
};
