import React, { useState, useEffect } from "react";
import { Table, Button, Card } from "flowbite-react";

export const KubeFlow = () => {
  const [containers, setContainers] = useState([]);

  const scanContainers = async () => {
    // Aquí debes implementar la lógica para escanear los contenedores de tu PC.
    // Esta función debe devolver una lista de contenedores.
    // Por ahora, vamos a simularlo con un array vacío.
    return [];
  };

  const handleScan = async () => {
    const containers = await scanContainers();
    setContainers(containers);
  };

  return (
    <div className="flex justify-center items-center h-screen">
      <Card className="w-full max-w-md">
        <Card.Header>
          <h2 className="text-xl font-bold">Contenedores</h2>
        </Card.Header>
        <Card.Body>
          <Button onClick={handleScan} color="primary" className="mb-4">
            Scan
          </Button>
          <Table hoverable>
            <Table.Head>
              <Table.HeadCell>Nombre del contenedor</Table.HeadCell>
              {/* Añade aquí más celdas de encabezado según los datos que quieras mostrar */}
            </Table.Head>
            <Table.Body className="divide-y">
              {containers.map((container, index) => (
                <Table.Row key={index}>
                  <Table.Cell>{container.name}</Table.Cell>
                  {/* Añade aquí más celdas según los datos que quieras mostrar */}
                </Table.Row>
              ))}
            </Table.Body>
          </Table>
        </Card.Body>
      </Card>
    </div>
  );
};
