import React, { useState } from "react";
import { Card, Button, TextInput } from "flowbite-react";

export const Kubeia = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [suggestions, setSuggestions] = useState([
    "How do I install Docker?",
    "How do I create a pod in Kubernetes?",
    "How do I scale an application in Kubernetes?",
    "How do I share data between Docker containers?"
  ]);

  const handleSend = () => {
    if (input) {
      setMessages([...messages, { text: input, sender: "user" }]);
      setInput("");
      setSuggestions([]);
    }
  };

  return (
    <div className="flex flex-col p-8">
      <Card className="w-full space-y-4 rounded-xl shadow-md dark:bg-gray-800">
        <div className="d-flex flex-col h-100">
          <div className="overflow-auto mb-3">
            {messages.map((message, index) => (
              <div key={index} className={`text-${message.sender === "user" ? "end" : "start"}`}>
                <Card className={`mb-2 ${message.sender === "user" ? "bg-primary text-white" : ""}`}>
                  {message.text}
                </Card>
              </div>
            ))}
          </div>
          <div className="d-flex flex-wrap mb-3">
            {suggestions.map((suggestion, index) => (
              <Card key={index} className="m-1 p-2 bg-light flex-grow-1 flex-basis-0">
                {suggestion}
              </Card>
            ))}
          </div>
          <div className="mt-auto d-flex align-items-center justify-content-between">
          <div className="flex-1">
              <TextInput 
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Type a message"
                onKeyPress={(event) => event.key === 'Enter' ? handleSend() : null}
                className="me-2"
              />
              <Button onClick={handleSend}>Send</Button>
            </div>
          </div>
        </div>
      </Card>
    </div>
  );
};
