import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

export const RightComponent = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [token, setToken] = useState("");
  const [userData, setUserData] = useState(null);
  const [serverStatus, setServerStatus] = useState("Waiting");
  const [serverVersion, setServerVersion] = useState("Unknown");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/login", {
        email,
        password,
      });
      const responseData = response.data;
      if (response.data.token) {
        localStorage.setItem("token", response.data.token);
        setToken(response.data.token);
      }
    } catch (error) {
      console.error(error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setToken("");
    setUserData(null);
  };

  useEffect(() => {
    const storedToken = localStorage.getItem("token");
    if (storedToken) {
      setToken(storedToken);
    }
  }, []);

  useEffect(() => {
    if (token) {
      axios
        .get("http://localhost:5000/user", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then((response) => {
          setUserData(response.data);
        })
        .catch((error) => {
          console.error(error);
        });
    }
  }, [token]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/status")
      .then((response) => {
        setServerStatus(response.data.status);
        setServerVersion(response.data.version);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  return (
    <div className="w-1/6">
      <div className="border border-gray-200 rounded-lg shadow">
        <div className="relative overflow-x-auto p-4">
          <h2 className="text-2xl font-bold mb-4 text-center">API Status</h2>
          <table className="w-full text-sm text-left text-gray-500 dark:text-gray-400">
            <tbody>
              <tr className="bg-white dark:bg-gray-800">
                <th
                  scope="row"
                  className="px-6 py-4 font-medium text-gray-900 whitespace-nowrap dark:text-white"
                >
                  Status
                </th>
                <td className="px-6 py-4">
                  {serverStatus === "UP" ? (
                    <span className="bg-green-100 text-green-800 text-xs font-medium mr-2 px-2.5 py-0.5 rounded dark:bg-green-900 dark:text-green-300">
                      {serverStatus}
                    </span>
                  ) : (
                    <span className="bg-red-100 text-red-800 text-xs font-medium mr-2 px-2.5 py-0.5 rounded dark:bg-red-900 dark:text-red-300">
                      {serverStatus}
                    </span>
                  )}
                </td>
              </tr>
              <tr className="bg-white dark:bg-gray-800">
                <th
                  scope="row"
                  className="px-6 py-4 font-medium text-gray-900 whitespace-nowrap dark:text-white"
                >
                  Version
                </th>
                <td className="px-6 py-4">{serverVersion}</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};
