import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { Avatar, Navbar, Dropdown} from 'flowbite-react';

export const NavbarRC = () => {
  const [authenticated, setAuthenticated] = useState(false);
  const [storedToken, setStoredToken] = useState("");
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    const storedToken = localStorage.getItem("token");
    if (storedToken) {
      setAuthenticated(true);
    }
  }, []);

  const handleSignOut = () => {
    localStorage.removeItem("token");
    setAuthenticated(false);
  }

  useEffect(() => {
    const storedToken = localStorage.getItem("token");
    if (storedToken) {
      axios
        .get("http://localhost:5000/userData", {
          headers: {
            Authorization: `Bearer ${storedToken}`,
          },
        })
        .then((response) => {
          setUserData(response.data.data);
        })
        .catch((error) => {
          console.error(error);
        });
    }
  }, [storedToken]);

  return (
    <Navbar fluid rounded>
      <Navbar.Brand href="https://flowbite-react.com">
        <img src="https://cdn-icons-png.flaticon.com/128/1344/1344707.png" className="mr-3 h-6 sm:h-9" alt="Flowbite React Logo" />
        <span className="self-center whitespace-nowrap text-xl font-semibold dark:text-white">Dashboard</span>
      </Navbar.Brand>
      <div className="flex md:order-2">
        <Dropdown
          arrowIcon={false}
          inline
          label={
            <Avatar alt="User settings" img="https://cdn-icons-png.flaticon.com/128/1077/1077012.png" rounded />
          }
        >
          <Dropdown.Header>
            <span className="block text-sm">{userData ? userData.fullname : ""}</span>
            <span className="block truncate text-sm font-medium">{userData ? userData.email : ""}</span>
          </Dropdown.Header>
          <Dropdown.Item>Dashboard</Dropdown.Item>
          <Dropdown.Item>Settings</Dropdown.Item>
          <Dropdown.Item>Earnings</Dropdown.Item>
          <Dropdown.Divider />
          <Dropdown.Item onClick={handleSignOut}>
            Sign out
          </Dropdown.Item>
        </Dropdown>
        <Navbar.Toggle />
      </div>
      <Navbar.Collapse>
        <Navbar.Link href="#" active>
          <Link to="/">KubeFlow</Link>
        </Navbar.Link>
        <Navbar.Link href="#">KubeSh</Navbar.Link>
        <Navbar.Link>
          <Link to="/kubehub">KubeFlow</Link>
        </Navbar.Link>
        <Navbar.Link href="#">
          <Link to="/kubeia">KubeIA</Link>
        </Navbar.Link>
      </Navbar.Collapse>
    </Navbar>
  );
};
